n();?>            </div>        </div>		<?php $chipTuning->renderDescription();?>		<?php $chipTuning->renderGallery();?>		<?php //$chipTuning->renderLinks();?>		<?php $chipTuning->renderVideo();?><!--        <hr class="uk-article-divider">-->    </article><?php endforeach;?>