<?phpdefined('_JEXEC') or die();JImport('b0.Post.Posts');/** @var JRegistry $paramsList */$paramsList = $this->tmpl_params['list'];$posts = new Posts($this->items, $paramsList);?><?php foreach ($posts->items as $id => $item): ?>	<article class="uk-article uk-visible-hover">        <!-- Панель управления -->		<?php if($item->controls){			echo JLayoutHelper::render('b0.controls-list', $item->controls);		}?>        <h2 class="uk-article-title uk-text-center-small">            <a href="<?= JRoute::_($item->url);?>" target="_blank">				<?= $item->title?>            </a>        </h2>		<div class="uk-grid" data-uk-grid-margin>			<div class="uk-width-medium-1-5 uk-text-center-small">				<?= $item->image;?>			</div>			<div class="uk-width-medium-4-5 uk-text-center-small">		        <p class="uk-article-meta">		            <?= $item->ctime;?>		        </p>		        <p class="uk-article-lead">			        <?= $item->announcement;?>		        </p>			</div>		</div>	</article><?php endforeach;?>