<?phpdefined('_JEXEC') or die('Restricted access');JImport('b0.Work.WorkKeys');//JImport('b0.fixtures');JImport('b0.pricehelper');//$params = $this->tmpl_params['list'];//$user = $this->user;?><?php foreach ($this->items as $item):?>	<article class="uk-article uk-visible-hover">		<!-- Панель управления -->		<?php if($item->controls){			echo JLayoutHelper::render('b0.controls-list', $item->controls);		}?>		<!-- Работа -->		<h2 class="uk-article-title">			<a href="<?= JRoute::_($item->url);?>" target="_blank"><?= $item->title?></a>        </h2>        <div class="uk-grid">			<div class="uk-width-medium-7-10 uk-hidden-small">				<?php if (isset($item->fields_by_key[WorkKeys::KEY_SUBTITLE]))				    echo '<p class="uk-article-meta">'.$item->fields_by_key[WorkKeys::KEY_SUBTITLE]->result.'</p>';?>			</div>			<div class="uk-width-medium-3-10 uk-width-small-1-1">                <?php                $is_special = ($item->fields_by_key[WorkKeys::KEY_IS_SPECIAL]->value == 1) ? true : false;                $prices = ['general'=> $item->fields_by_key[WorkKeys::KEY_PRICE_GENERAL]->value,                    'special'=> $item->fields_by_key[WorkKeys::KEY_PRICE_SPECIAL]->value ?? 0];                if ($is_special) {                    echo '<p class="ls-price-special uk-text-danger">Специальная цена: ' . render_price($prices['special']) . '</p>';                    echo '<p class="ls-price-second">Обычная цена: <del>' . render_price($prices['general']) . '</del></p>';                    echo '<p>Вы экономите ' . render_economy($prices['general'], $prices['special']).'</p>';                }                else {                    echo '<p class="ls-price-second">Цена: ' . render_price($prices['general']) . '</p>';                } ?>			</div>		</div>	</article><?php endforeach;?><hr class="uk-article-divider">