<?phpdefined('_JEXEC') or die('Restricted access');JImport('logan.pricehelper');$params = $this->tmpl_params['list'];$user = $this->user;?><?php foreach ($this->items as $item):?>	<article class="uk-article uk-visible-hover">		<!-- Панель управления -->		<?php if($user->get('id')):?>			<div class="uk-hidden uk-button-group uk-float-right" >				<?php if ($item->controls):?>					<div class="uk-float-right">						<div class="uk-button-dropdown" data-uk-dropdown="{mode:'click'}">							<button class="uk-button-link">								<i class="uk-icon-cogs uk-icon-small"></i>							</button>							<div class="uk-dropdown uk-dropdown-small">								<ul class="uk-nav uk-nav-dropdown uk-panel uk-panel-box uk-panel-box-secondary">									<?= list_controls($item->controls);?>								</ul>							</div>						</div>					</div>				<?php endif;?>			</div>		<?php endif;?>		<!-- Работа -->		<h2 class="uk-article-title">			<a href="<?= JRoute::_($item->url);?>" target="_blank"><?= $item->title?></a>        </h2>        <div class="uk-grid">			<div class="uk-width-medium-7-10 uk-hidden-small">				<?= '<p class="ls-sub-title">для ' . $item->fields_by_id[11]->result . '</p>'; ?>				<?php if (isset($item->fields_by_id[64])) echo '<p class="uk-article-meta">'.$item->fields_by_id[64]->result.'</p>';?>			</div>			<div class="uk-width-medium-3-10 uk-width-small-1-2">                <?php                $is_featured = (boolean) $item->featured;                $prices = ['general'=> $item->fields_by_id[5]->value,                    'special'=> isset($item->fields_by_id[65]) ? ($item->fields_by_id[65]->value) : 0];                if ($is_featured) {                    echo '<p class="ls-price-special uk-text-danger">Специальная цена: ' . render_price($prices['special']) . '</p>';                    echo '<p class="ls-price-second">Обычная цена: <del>' . render_price($prices['general']) . '</del></p>';                    echo '<p>Вы экономите ' . render_economy($prices['general'], $prices['special']).'</p>';                }                else {                    echo '<p class="ls-price-second">Цена: ' . render_price($prices['general']) . '</p>';                } ?>			</div>		</div>	</article><?php endforeach;?><hr class="uk-article-divider">